SELECT Products‘.*, ProductUseIntentionForFranchisee‘.Product_Use_Intention_For_Franchisee_Description, ProductType‘.Type_Description, ProductCategory‘.Category_Description, SalesCategoryT.Sales_Category_Description, MeasureUnitsT.Measure_Unit_Abbreviation AS Measure_Unit_Abbreviation_for_Content_In_Package, MeasureUnitsT_1.Measure_Unit_Abbreviation AS Measure_Unit_Abbreviation_for_Purchases_Sales
FROM SalesCategoryT RIGHT JOIN (ProductUseIntentionForFranchisee‘ RIGHT JOIN (ProductType‘ RIGHT JOIN (ProductCategory‘ RIGHT JOIN (MeasureUnitsT RIGHT JOIN (MeasureUnitsT AS MeasureUnitsT_1 RIGHT JOIN Products‘ ON MeasureUnitsT_1.Measure_Unit_ID = Products‘.[Measure_Unit_For_Purchases/Sales_ID]) ON MeasureUnitsT.Measure_Unit_ID = Products‘.Measure_Unit_Of_Product_Content_In_Package_ID) ON ProductCategory‘.Category_ID = Products‘.Product_Category_ID) ON ProductType‘.Type_ID = Products‘.Product_Type_ID) ON ProductUseIntentionForFranchisee‘.Product_Use_Intention_For_Franchisee_ID = Products‘.Product_Use_Intention_For_Franchisee_ID) ON SalesCategoryT.Sales_Category_ID = Products‘.Sales_Category_ID;

