UPDATE RecalculatedInventoryBalancePerProductTransactorFromRecordsTempT INNER JOIN ProductInventoryBalanceT ON (RecalculatedInventoryBalancePerProductTransactorFromRecordsTempT.Transactor_ID = ProductInventoryBalanceT.Transactor_ID) AND (RecalculatedInventoryBalancePerProductTransactorFromRecordsTempT.Product_ID = ProductInventoryBalanceT.Product_ID) SET ProductInventoryBalanceT.Total_Debit = [RecalculatedInventoryBalancePerProductTransactorFromRecordsTempT].[SumOfDebit], ProductInventoryBalanceT.Total_Credit = [RecalculatedInventoryBalancePerProductTransactorFromRecordsTempT].[SumOfCredit];

